    public static Jdbi jdbi = Jdbi.create("jdbc:mysql://10.34.105.19/dart", "stage-dart", "fngsgj");
    HashMap<String, Schema> fileToSchema = new HashMap<>();
    HashMap<Schema, Validator.NodeDef> schemaToDefMap = new HashMap<>();
    Validator validator = new Validator();
    long errorCount = 0;
    long total = 0;
    Validator.NodeDef dummy = new Validator.NodeDef("sdaf");
    public void go(String logFile, Object obj) {
//        log.info("validating {} {}", logFile, obj);
        if(!fileToSchema.containsKey(logFile)) {
            fileToSchema.put(logFile, parse(logFile));
        }

        Schema schema = fileToSchema.get(logFile);
        if(schema == dummySchema) {
            return;
        }
        String payload = (String)obj;
        JsonNode jsonNode = null;
        try {
            jsonNode = mapper.readTree(payload);
        } catch (IOException e) {
            log.error("", e);
            return;
        }
        String schemaVersion = jsonNode.get("schemaVersion").asText();
        schema.version = schemaVersion;
        if(!schemaToDefMap.containsKey(schema)) {
            try {
                String populate = populate(schema);
                if(populate.length()==0) {
                    return;
                }
                schemaToDefMap.put(schema, validator.preProcess(populate));
            } catch (IOException e) {

                log.error("", e);
                return;
            } catch (Exception e) {
                return;
            }
        }
        Validator.NodeDef nodeDef = schemaToDefMap.get(schema);
        Validator.ValidationError error = validator.validate(jsonNode.get("data"), nodeDef);
        total++;
        if(error.errors != null) {
            errorCount++;
            log.error("schema:{} payload:{}\nerror:{}", schema, payload, error.errors);
        }

    }

