    private String populate(Schema schema) {
        try {
            String jsonDef = jdbi.withHandle(handle ->
                    handle.createQuery("select id.json_def from ingestion_entity ie, ingestion_def id where ie.company=:cc and ie.org=:oo and ie.namespace=:nn and ie.name=:mm and version=:vv and ie.id = id.`ingestion_id`")
                            .bind("cc", schema.company)
                            .bind("oo", schema.org)
                            .bind("nn", schema.namespace)
                            .bind("mm", schema.name)
                            .bind("vv", schema.version)
                            .mapTo(String.class)
                            .findOnly());
//        log.info("jsondef is {}", jsonDef);
            return jsonDef;

        } catch (Exception e) {
            return "";
        }
    }

    Schema dummySchema = new Schema("", "", "", "");
    private Schema parse(String logFile) {
//        log.info("{}", logFile);
        try {
            Path path = Paths.get(logFile);
            String s = path.getParent().getFileName().toString();
            String[] split = s.split("\\.");
            return new Schema(split[1], split[2], split[3], split[4].split("-")[split[4].split("-").length - 2]);
        } catch (Exception e) {
            return dummySchema;
        }
    }


    public static class Schema {
        public String company, namespace, org, name, version;

        public Schema(String company, String org, String namespace, String name) {
            this.company = company;
            this.namespace = namespace;
            this.org = org;
            this.name = name;
        }

        @Override
        public String toString() {
            return "Schema{" +
                    "company='" + company + '\'' +
                    ", namespace='" + namespace + '\'' +
                    ", org='" + org + '\'' +
                    ", name='" + name + '\'' +
                    ", version='" + version + '\'' +
                    '}';
        }

        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (o == null || getClass() != o.getClass()) return false;
            Schema schema = (Schema) o;
            return Objects.equals(company, schema.company) &&
                    Objects.equals(namespace, schema.namespace) &&
                    Objects.equals(org, schema.org) &&
                    Objects.equals(name, schema.name) &&
                    Objects.equals(version, schema.version);
        }

        @Override
        public int hashCode() {
            return Objects.hash(company, namespace, org, name, version);
        }
    }
